  align-items: center;
  height: 100vh;
  background: #3f81b3 url(https://彭威翔.台灣/images/bg.jpg) no-repeat center;
  background-size: cover;
  overflow: hidden;
  z-index: 0;
  position: relative;
}

.w3l-errorhny-main::before {
  content: "";
  background: rgb(1 12 21 / 40%);
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: -1;
}

.w3l-errorhny-main .errorhnyhny-info {
  text-align: center;
  max-width: 500px;
  position: absolute;
  left: 0;
  right: 0;
  margin: 2em auto 0;
  z-index: 99;
}

.w3l-errorhny-main h1 {
  position: relative;
  font-size: calc(10px + 13vw);
  font-weight: 700;
  color: #f5f5f5;
  letter-spacing: 0.02em;
  text-transform: uppercase;
  perspective: 500px;
}

.w3l-errorhny-main h2 {
  color: #fff;
  font-weight: 700;
  font-size: 28px;
  margin-bottom: 10px;
  line-height: 38px;
