  align-items: center;
  height: 100vh;
  background: #3f81b3 url(https://彭威翔.台灣/images/bg.jpg) no-repeat center;
  background-size: cover;
  overflow: hidden;
  z-index: 0;
  position: relative;
}

.w3l-errorhny-main::before {
  content: "";
  background: rgb(1 12 21 / 40%);
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: -1;
}

.w3l-errorhny-main .errorhnyhny-info {
  text-align: center;
  max-width: 500px;
  position: absolute;
  left: 0;
  right: 0;
  margin: 2em auto 0;
  z-index: 99;
}

.w3l-errorhny-main h1 {
  position: relative;
  font-size: calc(10px + 13vw);
  font-weight: 700;
  color: #f5f5f5;
  letter-spacing: 0.02em;
  text-transform: uppercase;
  perspective: 500px;
}

.w3l-errorhny-main h2 {
  color: #fff;
  font-weight: 700;
  font-size: 28px;
  margin-bottom: 10px;
  line-height: 38px;
}

.w3l-errorhny-main p {
  color: #fff;
  opacity: 0.8;
}

.w3l-errorhny-main h1::before,
.w3l-errorhny-main h1::after {
  content: attr(aria-label);
  position: absolute;
  top: 0;
  left: 0;
  transform: translate(-50%, -50%);
  text-shadow: 0.01em 0.01em 0.01em rgba(0, 0, 0, 0.3);
}

.w3l-errorhny-main h1::before {
  animation: floatAbove 3.5s ease-in-out infinite;
  -webkit-clip-path: polygon(0% 0%, 100% 0%, 100% 50%, 0% 50%);
  clip-path: polygon(0% 0%, 100% 0%, 100% 50%, 0% 50%);
}

.w3l-errorhny-main h1::after {
  opacity: 0.65;
  filter: blur(0.02em);
  transform: translate(-50%, -50%) rotateX(21deg);
  animation: floatBelow 3.5s ease-in-out infinite;
  -webkit-clip-path: polygon(0% 50%, 100% 50%, 100% 100%, 0% 100%);
  clip-path: polygon(0% 50%, 100% 50%, 100% 100%, 0% 100%);
}

@keyframes floatAbove {
  50% {
    transform: translate(-50%, -60%);
    -webkit-clip-path: polygon(0% 0%, 100% 0%, 100% 60%, 0% 60%);
    clip-path: polygon(0% 0%, 100% 0%, 100% 60%, 0% 60%);
  }
}

@keyframes floatBelow {
  50% {
    transform: translate(-50%, -60%) rotateX(10deg);
    -webkit-clip-path: polygon(0% 60%, 100% 60%, 100% 100%, 0% 100%);
    clip-path: polygon(0% 60%, 100% 60%, 100% 100%, 0% 100%);
  }
}
